choose.when:
update popup set title = #{title}, content = #{content}, popup_date = sysdate, cupon_base = #{cupon}, onoff = 0

	, img_uuid = #{uuid}

where popup_num = #{num}

choose.otherwise:
update popup set title = #{title}, content = #{content}, popup_date = sysdate, cupon_base = (null), onoff = 0

	, img_uuid = #{uuid}

where popup_num = #{num}

mapper.select:
select rr.* from (select rs.*, rownum rnum from (select rst.num, rst.cupon_type, rst.end_date, rst.cupon_base, rst.rate, nvl(rst.uu, 0) uu, nvl(rst.u, 0) u from (select qw.*, c.rate from (select bc.*, cb.u from (select * from CUPON_BASE b left join (select CUPON_BASE, count(*) uu from cupon_list group by CUPON_BASE) cu on b.num = cu.CUPON_BASE)bc left join (select CUPON_BASE, count(*) u from popup group by CUPON_BASE)cb on bc.num = cb.CUPON_BASE) qw left join cupon_type c on qw.cupon_type = c.CUPON_NUM)rst where end_date > sysdate
and rate = #{value}
order by end_date
asc
desc
)rs)rr where rnum between #{start} and #{end}
