select * from (select tmp1.*, rownum as r from (select ordertbl.*, dhl.charge as charge, state.STATE as st,cupon_type.rate as rate from ordertbl join producttbl on ordertbl.pro_num = producttbl.pro_num join dhl on ordertbl.dhl = dhl.num join state on ordertbl.state = state.state_num join cupon_type on ordertbl.cupon_type = cupon_type.cupon_num where ordertbl.pro_num = any(select pro_num from producttbl where seller_id = #{id})) tmp1
where
state = #{state}
and
${search_type}=#{search_word}
) where r between #{start} and #{end} order by order_num desc

select count(*) from (select tmp1.*, rownum as r from (select ordertbl.*, dhl.charge as charge, state.STATE as st,cupon_type.rate as rate from ordertbl join producttbl on ordertbl.pro_num = producttbl.pro_num join dhl on ordertbl.dhl = dhl.num join state on ordertbl.state = state.state_num join cupon_type on ordertbl.cupon_type = cupon_type.cupon_num where ordertbl.pro_num = any(select pro_num from producttbl where seller_id = #{id})) tmp1
where
state = #{state}
and
${search_type}=#{search_word}
